import React, { useState, useEffect } from "react";
import ApiClient from "../../client/ApiClient"; // Assuming ApiClient is used to handle API requests
import Product from "./Product"; // Assuming this is the Product component where individual product details are displayed

const ProductList = () => {
  const { categoryId } = useParams(); // Get categoryId from the URL
  const [products, setProducts] = useState([]);

  const getProducts = () => {
    ApiClient.get(`/products/category/${categoryId}`)
      .then((response) => {
        console.log(response.data);
        setProducts(response.data);
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    getProducts();
  }, [categoryId]); // Fetch products whenever categoryId changes

  return (
    <div className="container">
      <h2 className="text-center">Products in Category {categoryId}</h2>
      <div className="row">
        {products.map((product, index) => (
          <Product data={product} key={index} /> // Render each product component
        ))}
      </div>
    </div>
  );
};

export default ProductList;
